    description: "Descri√ß√£o breve dos m√≥dulos",
    author: "Rodrigo Oliveira Badega",
    views: 5,
    comments: 0,
    time: "1 min de leitura",
    date: "h√° 21 horas",
    image: "", // Caminho da imagem ser√° adicionado futuramente
  },
  {
    id: 2,
    title: "Material 2",
    description: "Descri√ß√£o breve do material 2",
    author: "Rodrigo Oliveira Badega",
    views: 0,
    comments: 0,
    time: "1 min de leitura",
    date: "h√° 21 horas",
    image: "", // Caminho da imagem ser√° adicionado futuramente
  },
];

const CoursesPage = () => {
  return (
    <div className="courses-container">
      {courses.map((course) => (
        <div key={course.id} className="course-card">
          {/* √Årea da imagem */}
          <div className="course-image">
            {course.image ? (
              <img src={course.image} alt={course.title} />
            ) : (
              <div className="placeholder-icon">
                <span>üìò</span>
              </div>
            )}
          </div>

          {/* √Årea de informa√ß√µes */}
          <div className="course-info">
            <p className="course-author">
              {course.author} ‚Ä¢ {course.date} ‚Ä¢ {course.time}
            </p>
            <h2 className="course-title">{course.title}</h2>
            <p className="course-description">{course.description}</p>
            <div className="course-stats">
              <span>{course.views} visualiza√ß√µes</span>
              <span>{course.comments} coment√°rios</span>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default CoursesPage;
