// src/App.jsx
import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Nav from './components/nav/Nav'; // Componente de navegação
import Footer from './components/footer/Footer'; // Componente de rodapé
import HomePage from './pages/HomePage/HomePage'; // Página inicial
import AboutPage from './pages/AboutPage/AboutPage'; // Página sobre

function App() {
  return (
    <Router>
      <div className="App">
        <Nav /> {/* A navegação será renderizada em todas as páginas */}

        <main>
          <Routes>
            <Route path="/" element={<HomePage />} /> {/* Rota para a página inicial */}
            <Route path="/about" element={<AboutPage />} /> {/* Rota para a página sobre */}
          </Routes>
        </main>

        <Footer /> {/* O rodapé será renderizado em todas as páginas */}
      </div>
    </Router>
  );
}

export default App;
