// src/App.jsx
import React from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom"; // Modifiquei o import
import Nav from "./components/nav/Nav"; 
import Footer from "./components/footer/footer.jsx";
import HomePage from "./pages/HomePage/HomePage"; // Importando a HomePage
import AboutPage from "./pages/AboutPage/AboutPage"; // Importando a AboutPage
import CoursesPage from "./pages/CoursesPage/CoursesPage"; // Importando a CoursesPage

import "./index.css"; // Estilos globais

function App() {
  return (
    <Router>
      <div className="App">
        {/* Cabeçalho - Componente de Navegação */}
        <Nav />

        {/* Roteamento das páginas */}
        <Routes>
          <Route path="/" element={<HomePage />} /> {/* Página inicial */}
          <Route path="/about" element={<AboutPage />} /> {/* Página Sobre */}
          <Route path="/courses" element={<CoursesPage />} /> {/* Página de Cursos */}
          {/* Adicione mais rotas conforme necessário */}
        </Routes>

        {/* Rodapé */}
        <Footer />
      </div>
    </Router>
  );
}

export default App;
