import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Nav.css';

// Definindo tipos para as propriedades (se houver futuras)
interface NavProps {}

const Nav: React.FC<NavProps> = () => {
  const [isOpen, setIsOpen] = useState<boolean>(false); // Tipagem explícita para o estado

  // Alternar o estado do menu
  const toggleMenu = (): void => {
    setIsOpen((prevState) => !prevState);
  };

  // Fechar o menu ao clicar em um link
  const closeMenu = (): void => {
    setIsOpen(false);
  };

  return (
    <header className="nav-container">
      <nav className="nav-content">
        {/* Logo */}
        <div className="nav-logo">
          <Link to="/" onClick={closeMenu}>
            IDCTEC
          </Link>
        </div>

        {/* Links do menu */}
        <ul className={`nav-links ${isOpen ? 'open' : ''}`}>
          <li>
            <Link to="/" onClick={closeMenu}>
              Página Inicial
            </Link>
          </li>
          <li>
            <Link to="/courses" onClick={closeMenu}>
              Cursos
            </Link>
          </li>
          <li>
            <Link to="/about" onClick={closeMenu}>
              Quem Somos
            </Link>
          </li>
          <li>
            <Link to="/partners" onClick={closeMenu}>
              Parcerias
            </Link>
          </li>
        </ul>

        {/* Botão do menu (hambúrguer) */}
        <button className="nav-toggle" onClick={toggleMenu} aria-label="Abrir Menu">
          <div className={`hamburger ${isOpen ? 'open' : ''}`}>
            <span></span>
            <span></span>
            <span></span>
          </div>
        </button>
      </nav>
    </header>
  );
};

export default Nav;
