// src/App.jsx
import React from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom"; // Modifiquei o import
import Nav from "./components/nav/Nav"; 
import Footer from "./components/footer/footer.jsx";
import HomePage from "./pages/HomePage/HomePage"; // Importando a HomePage

import "./index.css"; // Estilos globais

function App() {
  return (
    <Router>
      <div className="App">
        {/* Cabeçalho - Componente de Navegação */}
        <Nav />
        <h1>Sobre Nós</h1>
      <p>
        Bem-vindo à nossa página de sobre! Somos uma equipe dedicada a oferecer
        serviços e cursos para aprimorar suas habilidades em tecnologia.
      </p>
        {/* Roteamento das páginas */}
        <Routes>
          <Route path="/" element={<HomePage />} /> {/* Alterei a sintaxe para element */}
          {/* Adicione novas rotas para outras páginas aqui */}
        </Routes>

        {/* Rodapé */}
        <Footer />
      </div>
    </Router>
  );
}

export default App;
