// src/components/nav/Nav.jsx
import React, { useState } from "react";
import "./Nav.css"; // Importando o arquivo de estilo do componente

const Nav = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <header className="nav-container">
      <nav className="nav-content">
        {/* Logo fixo à esquerda */}
        <div className="nav-logo">IDCTEC</div>

        {/* Menu de navegação no meio */}
        <ul className={`nav-links ${isOpen ? "open" : ""}`}>
          <li><a href="#home">Página Inicial</a></li>
          <li><a href="#courses">Cursos</a></li>
          <li><a href="#about">Quem Somos</a></li>
          <li><a href="#partners">Parcerias</a></li>
        </ul>

        {/* Botão de hambúrguer para dispositivos móveis */}
        <button
          className="nav-toggle"
          onClick={() => setIsOpen(!isOpen)}
        >
          <span className="nav-icon"></span>
        </button>
      </nav>
    </header>
  );
};

export default Nav;
