// src/App.jsx
import React from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Nav from "./components/nav/Nav"; 
import Footer from "./components/footer/footer.jsx";
import HomePage from "./pages/HomePage/HomePage"; // Importando a HomePage
import AboutPage from "./pages/AboutPage/AboutPage"; // Importando a AboutPage

import "./index.css"; // Estilos globais

function App() {
  return (
    <Router>
      <div className="App">
        {/* Cabeçalho - Componente de Navegação */}
        <Nav />

        {/* Roteamento das páginas */}
        <Switch>
          <Route path="/" exact component={HomePage} />
          <Route path="/about" component={AboutPage} />
          {/* Adicione novas rotas para outras páginas aqui */}
        </Switch>

        {/* Rodapé */}
        <Footer />
      </div>
    </Router>
  );
}

export default App;
