import React, { useState } from "react";
import { useHistory } from "react-router-dom"; // Para redirecionar para o material
import "./CoursesPage.css"; // Importa√ß√£o do CSS

const courses = [
  {
    id: 1,
    title: "Material 1",
    description: "Descri√ß√£o breve dos m√≥dulos",
    author: "Rodrigo Oliveira Badega",
    views: 5,
    time: "1 min de leitura",
    date: "h√° 21 horas",
    image: "", // Caminho da imagem ser√° adicionado futuramente
  },
  {
    id: 2,
    title: "Material 2",
    description: "Descri√ß√£o breve do material 2",
    author: "Rodrigo Oliveira Badega",
    views: 0,
    time: "1 min de leitura",
    date: "h√° 21 horas",
    image: "", // Caminho da imagem ser√° adicionado futuramente
  },
];

const CoursesPage: React.FC = () => {
  const [courseViews, setCourseViews] = useState(courses);
  const history = useHistory(); // Para redirecionar para a p√°gina do curso

  const handleClickCourse = (id: number) => {
    // Atualiza as visualiza√ß√µes do curso
    setCourseViews((prevCourses) =>
      prevCourses.map((course) =>
        course.id === id
          ? { ...course, views: course.views + 1 }
          : course
      )
    );

    // Redireciona para o curso (voc√™ pode ajustar a URL conforme necess√°rio)
    history.push(`/courses/${id}`);
  };

  return (
    <div className="courses-container">
      {courseViews.map((course) => (
        <div key={course.id} className="course-card" onClick={() => handleClickCourse(course.id)}>
          {/* √Årea da imagem */}
          <div className="course-image">
            {course.image ? (
              <img src={course.image} alt={course.title} />
            ) : (
              <div className="placeholder-icon">
                <span>üìò</span>
              </div>
            )}
          </div>

          {/* √Årea de informa√ß√µes */}
          <div className="course-info">
            <h2 className="course-title">{course.title}</h2>
            <p className="course-description">{course.description}</p>
            <p className="course-author">{course.author}</p> {/* Autor abaixo da descri√ß√£o */}
            <div className="course-stats">
              <span>{course.views} visualiza√ß√µes</span>
              <span>{course.time}</span>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default CoursesPage;
